volume.script:
if (upColor === 0) {
  if (options.showDelta) {
    upColor = options.upBgColor
    downColor = options.downBgColor
  } else {
    upColor = options.upColor
    downColor = options.downColor
  }
}

if (options.showDelta) {
  plothistogram({ time: time, value: Math.abs(vbuy-vsell), color: vbuy - vsell > 0 ? options.upColor : options.downColor})
}

plothistogram({ time: time, value: vbuy + vsell, color: vbuy > vsell ? upColor : downColor })

