volume.script:
volume = vbuy + vsell
delta = vbuy - vsell

getStDevAboveAvg = function(v) {
  a = sma(Math.pow(v,2),options.length)
  b = Math.pow(sum(v,options.length),2)/Math.pow(options.length,2)
  stdev = Math.sqrt(a - b)
  avg = sma(v, options.length)
  return avg+stdev*options.mult
}

vThreshold = getStDevAboveAvg(volume)
absDelta = Math.abs(delta)
deltaThreshold = getStDevAboveAvg(absDelta)

volumeColor = vbuy > vsell ? options.upColor : options.downColor
if (volume > vThreshold)
  volumeColor = vbuy > vsell ? options.upBgColor : options.downBgColor

plothistogram({
  time: time,
  value: volume,
  color: volumeColor
})

if (options.showDelta && absDelta > deltaThreshold) {
  plothistogram({ time: time, value: absDelta, color: options.deltaColor})
}

